import React from "react";
import { PropTypes } from "prop-types";
import Link from 'next/link';
import { Menu, Input, Row, Col } from 'antd';
import UserProfile from './UserProfile';
import LoginForm from './LoginForm';
import styled, { createGlobalStyle } from 'styled-components';
import { useSelector } from 'react-redux';

const SearchInput = styled(Input.Search)`
vertical-align: middle;
`
const Global = createGlobalStyle`
.ant-row{
    margin-right: 0 !important;
    margin-left: 0 !important;
}

.ant-col:first-child{
    padding-left:0 !important;
}

.ant-col:last-child{
    padding-right:0 !important;
}

`


const AppLayout = ({ children }) => {
    const {me} = useSelector((state) => state.user);

    return (
        <div>
            <Global/>
            <Menu mode='horizontal'>
                <Menu.Item key="1">
                    <Link href='/'><a>노드버드</a></Link>
                </Menu.Item>
                <Menu.Item key="2">
                    <Link href='/profile'><a>profile</a></Link>

                </Menu.Item>
                <Menu.Item key="3">
                    <SearchInput enterButton />
                </Menu.Item>
                <Menu.Item key="4">
                    <Link href='/signup'><a>signup</a></Link>
                </Menu.Item>
            </Menu>
            <Row gutter={8}>
                <Col xs={24} md={6}>
                    {me ? <UserProfile /> : <LoginForm />}
                </Col>
                <Col xs={24} md={12}>{children}</Col>
                <Col xs={24} md={6}>
                    <a href="https://www.naver.com" target="_blank" rel="noreferrer">Go to naver</a>
                </Col>
            </Row>

        </div>
    );
};


AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
};

export default AppLayout;

import React, { useCallback, useEffect, useState } from "react";
import PropTypes from 'prop-types';
import { Form, Button, Input } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { ADD_COMMENT_REQUEST } from "../reducers/post";

const CommentForm = ({ post }) => {
    const dispatch = useDispatch();
    const id = useSelector((state) => state.user.me?.id);
    const {addCommentDone,addCommentLoading} = useSelector((state) => state.post);
    // const [commentText,onChnageCommentText] = useInput('');
    const [commentText, setCommentText] = useState('');

    const onChnageCommentText = useCallback((e) => {
        setCommentText(e.target.value)
    }, [])

    useEffect(()=>{

        if(addCommentDone){
            setCommentText('');
        }
    },[addCommentDone])

    const onSubmitComment = useCallback(() => {
     
        dispatch({
            type: ADD_COMMENT_REQUEST,
            data: { content: commentText, postId: post.id, userId: id }
        })

    }, [commentText, id])

    return (
        <Form onFinish={onSubmitComment}>
            <Form.Item>
                <Input.TextArea
                    value={commentText}
                    onChange={onChnageCommentText}
                    rows={4}
                />
                <Button 
                
                style={{position:'absolute',right:0,bottom:-40,zIndex:1}}
                loading={addCommentLoading}
                type='primary' htmlType='submit'>삐약</Button>


            </Form.Item>
        </Form>
    )
}

CommentForm.prototype = {
    post: PropTypes.object.isRequired
}
export default CommentForm;

import React, { useCallback } from "react";
import PropTypes from'prop-types';
import {Button} from 'antd'
import { useDispatch, useSelector } from "react-redux";
import { FOLLOW_REQUEST, UNFOLLOW_REQUEST } from "../reducers/user";


const FollowButton=({post})=>{
    const dispatch = useDispatch();
    const {me,followLoading,unFollowLoading} = useSelector((state)=>state.user);

    const isFollowing = me?.Followings.find((v)=>v.id ===post.User.id);
const onClickButton = useCallback(()=>{
    if(isFollowing){
        dispatch({type:UNFOLLOW_REQUEST,data:post.User.id, })
    }else{
        dispatch({type:FOLLOW_REQUEST,data:post.User.id,})
        
    }
},[isFollowing]);

if(post.User.id ===me.id){
    return null;
}
return (
<Button loading={followLoading ||unFollowLoading } onClick={onClickButton}>
{isFollowing? '언팔로우':'팔로우'}
</Button>
);

}



FollowButton.propTypes={
    post:PropTypes.object.isRequired
}


export default FollowButton;

import PropTypes from 'prop-types';
import React, { useState } from 'react';
import Slick from 'react-slick';
import {Overlay,CloseBtn,Global,Header,ImageWrapper,Indicator,SlickWrapper,} from './styles';

const ImagesZoom = ({ images, onClose }) => {
    const [currentSlide, setCurrentSlide] = useState(0);
    return (
        <Overlay>
            <Global/>
            <Header>
                <h1>상세이미지</h1>
                <CloseBtn onClick={onClose}>X</CloseBtn>
            </Header>
            <SlickWrapper>
                <div>
                    <Slick
                        initialSlide={0}
                        beforeChange={(slide) => setCurrentSlide(slide)}
                        infinite
                        arrows={false}
                        slidesToScroll={1}
                    >
                        {images.map((v) => (
                            <ImageWrapper key={v.src}>
                                <img src={`http://localhost:3065/${v.src}`} alt={v.src} />
                            </ImageWrapper>
                        ))}
                    </Slick>
                    <Indicator>
                        <div>
                            {currentSlide +1}
                            {' '}
                            /
                            {images.length}
                        </div>
                    </Indicator>
                </div>
            </SlickWrapper>
        </Overlay>

    )
}


ImagesZoom.protoType = {
    images: PropTypes.arrayOf(PropTypes.object).isRequired,
    onClose: PropTypes.func.isRequired
}

export default ImagesZoom;

import styled, { createGlobalStyle } from 'styled-components';
import { CloseOutlined } from '@ant-design/icons';



export const Overlay = styled.div`
position:'fixed;
z-index:5000;
top:0;
left:0;
right:0;
bottom:0;
`

export const Header = styled.header`
height:44px;
background:white;
position:relative;
padding:0;
text-align: center;
& h1{
    margin:0;
    font-size:17px;
    color:#333;
    line-height:44px;
}

`

export const CloseBtn = styled(CloseOutlined)`
position:absolute;
right:0;
top:0;
padding:15px;
line-height:14px;
cursor:pointer;
`

export const SlickWrapper = styled.div`
height:70%;
background:#090909;
`

export const ImageWrapper = styled.div`
padding:32px;
text-align:center;

&img{
    margin:0 auto;
    max-height: 350px;
    width:300px;
}
`
export const Indicator = styled.div`
text-align: center;

& >div{
    width:75px;
    height:30px;
    line-height:30px;
    border-radius: 15px;
    background: #313131;
    display: inline-block;
    text-align: center;
    color:white;
    font-size:15px;
}
`

export const Global = createGlobalStyle`
.slick-slide{
display:inline-block;
}

`

import React, { useCallback, useEffect, useMemo, useState } from "react";
import { Form, Input, Button } from 'antd';
import Link from 'next/link';
import styled from 'styled-components';

import { useDispatch, useSelector } from 'react-redux';
import { loginRequestAction } from "../reducers/user";

const ButtonWrapper = styled.div`
    margin-top:10px;
`


const LoginForm = () => {
    const dispatch = useDispatch();
    const { logInLoading,logInError } = useSelector((state) => state.user);
    const [email, setEmail] = useState('');
    const onChangeEmail = useCallback((e) => {
        setEmail(e.target.value);

    }, [email])

    const [password, setPassword] = useState('');

    useEffect(()=>{
        if(logInError){
            alert(logInError);
        }
    },[logInError])
    const onChangePassword = useCallback((e) => {
        setPassword(e.target.value);

    }, [password])


    const style = useMemo(() => ({ marginTop: 10 }), []);

    const onSubmitForm = useCallback(() => {
        console.log(email, password);
        dispatch(loginRequestAction({ email, password }));
    }, [email, password])

    const FormWrapper = styled(Form)`
padding: 10px;
    `;


    return (
        <FormWrapper onFinish={onSubmitForm}>
            <div>
                <label htmlFor="user-email">이메일</label>
                <br />
                <Input name="user-email" type="email" value={email} onChange={onChangeEmail} required />

            </div>
            <div>
                <label htmlFor="user-password">password</label>
                <br />
                <Input
                    name="user-password"
                    type="password"
                    value={password}
                    onChange={onChangePassword} />

            </div>
            <ButtonWrapper style={style}>
                <Button type="primary" htmlType="submit" loading={logInLoading}>로그인</Button>
                <Link href="/signup"><a><Button>회원가입</Button></a></Link>

            </ButtonWrapper>
        </FormWrapper>
    );

}




export default LoginForm;

import { Card,Comment, List,Button, Popover, Avatar } from "antd";
import React, { useCallback, useEffect, useState } from "react";
import { RetweetOutlined, MessageOutlined, HeartOutlined, EllipsisOutlined, HeartTwoTone } from '@ant-design/icons';
import { useDispatch, useSelector } from "react-redux";
import PropTypes from 'prop-types';
import PostImages from "./PostImages";
import CommentForm from "./CommentForm";
import PostCardContent from "./PostCardContent";
import { REMOVE_POST_REQUEST,LIKE_POST_REQUEST,UNLIKE_POST_REQUEST ,RETWEET_REQUEST, RETWEET_FAILURE} from "../reducers/post";
import FollowButton from "./FollowButton";


const PostCard = ({ post }) => {
    const dispatch = useDispatch();
    const {removePostLoading} = useSelector((state)=>state.post);
    
    const id = useSelector((state) => state.user.me?.id);

    const [commentFormOpened, setCommentFormOpened] = useState(false);



    const onLike = useCallback(() => {
        if(!id){
            alert('로그인이 필요합니다.')
        }
        return  dispatch({
            type:LIKE_POST_REQUEST,
            data: post.id,
        })
    }, [id])
    const onUnlike = useCallback(() => {
        if(!id){
            alert('로그인이 필요합니다.')
        }
        return   dispatch({
            type:UNLIKE_POST_REQUEST,
            data: post.id,
        })
    }, [id])

    const onToggleCOmment = useCallback(() => {
        setCommentFormOpened((prev) => !prev)
    }, [])

const onRemovePost=useCallback(()=>{
    if(!id){
        alert('로그인이 필요합니다.')
    }
    return dispatch({
        type:REMOVE_POST_REQUEST,
        data:post.id
    })
},[])

const liked = post.Likers.find((v)=>v.id===id);

const onRetweet = useCallback(()=>{
    if(!id){
        alert('로그인이 필요합니다.')
    }
    return dispatch({
        type:RETWEET_REQUEST,
        data:post.id

    })
},[id])
    return (
        <div style={{ marginTop: 10 }}>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetweetOutlined key='retweet' onClick={onRetweet}/>,


                    liked&&liked
                        ? <HeartTwoTone twoToneColor="#eb2f96" key="heart" onClick={onUnlike} />
                        : <HeartOutlined key='heart'onClick={onLike} />,

                    <MessageOutlined key='commnet' onClick={onToggleCOmment} />,
                    <Popover key='more' content={(
                        <Button.Group>
                            {id && post.User.id === id ?
                                <>
                                    <Button>수정</Button>
                                    <Button 
                                    onClick={onRemovePost}
                                    loading={removePostLoading}
                                    type='danger'>삭제</Button>
                                </>
                                : <Button>신고</Button>}

                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                ]}
                title={post.RetweetId ? `${post.User.nickname}님이 리트윗하셨습니다`: null}
                extra={id&&<FollowButton post={post}/>}
            >
                {post.RetweetId && post.RetweetId
                    ?(
                        <Card
                        cover={post.Retweet.Images[0] && <PostImages images={post.Retweet.Images} />}
                        >
                                 <Card.Meta
                        avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                        title={post.User.nickname}
                        description={<PostCardContent postData={post.Retweet.content}/>}
                         />
                        </Card>
                    )
                    :(

                        <Card.Meta
                        avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                        title={post.User.nickname}
                        description={<PostCardContent postData={post.content}/>} />

                    )

                
                }
  
            </Card>
            {commentFormOpened && (
                <div>
                   <CommentForm post={post}/>
                   <List
                   header={`${post.Comments.length}개의 댓글`}
                   itemLayout="horizontal"
                   dataSource={post.Comments}
                   renderItem={(item)=>(
                    <li>
                        <Comment
                        author={item.User&&item.User.nickname}
                        avatar={item.User&&<Avatar>{item.User.nickname[0]}</Avatar>}
                        content={item.content}
                        />
                           
                    </li>
                   )}
                   />

                </div>)}
            {/* <CommentForm/>
    <Comments/> */}
        </div >
    )

}


PostCard.propTypes = {
    post: PropTypes.shape({
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createAt: PropTypes.string,
        Comments: PropTypes.arrayOf(PropTypes.object),
        Images: PropTypes.arrayOf(PropTypes.object),
        Likers: PropTypes.arrayOf(PropTypes.object),
        Retweetid:PropTypes.number,
        Retweet:PropTypes.objectOf(PropTypes.any),
    }).isRequired
}

export default PostCard;

import React from "react";
import PropTypes from 'prop-types';
import Link from 'next/link';

const PostCardContent =({postData})=>{

    return(

        <div>
            {postData.split(/(#[^\s#]+)/g).map((v,i)=>{
                if(v.match(/(#[^\s#]+)/)){
                    return <Link href={`/hashtag/${v.slice(1)}`} key={i}><a>{v}</a></Link>
                }
               return v; 
            })}
        </div>
    )
}



PostCardContent.propTypes = {
    postData: PropTypes.string.isRequired
}




export default PostCardContent;

import { Form, Input, Button } from 'antd';
import  React,{ useCallback, useEffect, useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addPost,UPLOAD_IMAGES_REQUEST,REMOVE_IMAGE, ADD_POST_REQUEST } from '../reducers/post';

const PostForm = () => {
    const [text,setText] = useState('');
    const dispatch = useDispatch();
    const { imagePaths,addPostDone } = useSelector((state) => state.post);
    const imageInput = useRef();
    useEffect(()=>{

        if(addPostDone){
            setText('');
        }
    },[addPostDone])



    const onChangeText =useCallback((e)=>{
        setText(e.target.value);
    })


    const onSubmit = useCallback(() => {
        if(!text || !text.trim()){
            return alert('게시글을 작성하세요.');
        }

        const formData = new FormData();
        imagePaths.forEach((p)=>{
            formData.append('image',p);

        });
        formData.append('content',text);

      return  dispatch({
            type:ADD_POST_REQUEST,
            data:formData
        })
       
    }, [text,imagePaths]);

    const onClickImageUpload=useCallback(()=>{
imageInput.current.click();
    },[imageInput.current])

const onChangeImages = useCallback((e)=>{
    console.log('images',e.target.files);
    const imageFormData = new FormData();
    [].forEach.call(e.target.files,(f)=>{
        imageFormData.append('image',f);
    });
    dispatch({
        type:UPLOAD_IMAGES_REQUEST,
        data:imageFormData
    })
})

const onRemoveImage=useCallback((index)=>()=>{
    dispatch({
        type: REMOVE_IMAGE,
        data:index
    })
})
    return (
        <Form style={{ margin: '10px 0 20px' }} encType="multipart/form-data" onFinish={onSubmit}>
            <Input.TextArea
                value={text}
                onChange={onChangeText}
                maxLength={140}
                placeholder='어떤 신기한 일이 있었나요?'
            />
            <div>
                <input type='file' name='image' multiple hidden ref={imageInput} onChange={onChangeImages} />
                <Button onClick={onClickImageUpload}>이미지 업로드</Button>
                <Button type='primary' style={{ float: 'right' }} htmlType='submit'>짹짹</Button>

            </div>
            <div>
                {imagePaths.map((v,i) => (
                    <div key={v} style={{ display: 'inline-block' }}>
                        <img src={`http://localhost:3065/${v}`} style={{ width: '200px' }} alt={v} />
                        <div>
                            <Button onClick={onRemoveImage(i)}>제거</Button>
                        </div>


                    </div>
                ))}
            </div>
        </Form>
    );

}

export default PostForm;

import React, { useCallback, useState } from "react";
import PropTypes from 'prop-types';
import { PlusOutlined} from '@ant-design/icons';
import ImagesZoom from "./ImagesZoom";


const PostImages = ({ images }) => {

    const [showImagesZoom, setShowImagesZoom] = useState(false);
    const onZoom = useCallback(() => {
        setShowImagesZoom(true);
    }, [])

    const onClose  = useCallback(()=>{
        setShowImagesZoom(false)
    },[])

    if (images.length === 1) {
        return (
            <>
                <img role='presentation' scr={`http://localhost:3065/${images[0].src}`} alt={images[0].src} onClick={onZoom} />
                {showImagesZoom && <ImagesZoom  images={images} onClose={onClose}/>}
            </>
        )
    }
    if (images.length === 2) {
        return (
            <>
                <img role='presentation' width="50%" scr={`http://localhost:3065/${images[0].src}`} alt={images[0].src} onClick={onZoom} />
                <img role='presentation' width="50%" scr={`http://localhost:3065/${images[1].src}`} alt={images[1].src} onClick={onZoom} />
                {showImagesZoom && <ImagesZoom  images={images} onClose={onClose}/>}

            </>
        )
    }
    return (
        <>
            <div>
                <img role='presentation' width="50%" scr={`http://localhost:3065/${images[0].src}`} alt={images[0].src} onClick={onZoom} />
                <div
                    role="presentation"
                    style={{ display: 'inline-block', width: '50%', textAlign: 'center', verticalAlign: 'middle' }}
                    onClick={onZoom}
                >
                    <PlusOutlined />
                    <br />
                    {images.length - 1}
                    개의 사진 더보기


                </div>
            </div>
            {showImagesZoom && <ImagesZoom  images={images} onClose={onClose}/>}

        </>
    )
}



PostImages.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object),
}

export default PostImages;

import React, { useCallback } from "react";
import { Card, Avatar, Button } from 'antd';
import {useDispatch, useSelector} from 'react-redux';
import { logoutRequestAction } from "../reducers/user";

const UserProfile = () => {
    const dispatch = useDispatch();
    const {me,logOutLoading} = useSelector((state)=>state.user);
const onLogOut = useCallback(()=>{
    dispatch(logoutRequestAction());
})

    return (
        <Card
            actions={[
                <div key="twit">짹쨱<br />{me.Posts.length}</div>,
                <div key="followings">followings<br />{me.Followings.length}</div>,
                <div key="followers">followers<br />{me.Followers.length}</div>,
            ]}
        >
            <Card.Meta
                title={me.nickname}
