import axios from "axios";
import { all, delay, put, fork, takeLatest, throttle } from "redux-saga/effects";
import { generateDummyPost,REMOVE_POST_SUCCESS,ADD_POST_SUCCESS, ADD_POST_FAILURE, ADD_POST_REQUEST, ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE, REMOVE_POST_REQUEST, LOAD_POSTS_REQUEST, LOAD_POSTS_SUCCESS, LOAD_POSTS_FAILURE } from "../reducers/post";
import shortid from "shortid";
import { ADD_POST_TO_ME, FOLLOW_FAILURE, FOLLOW_REQUEST, FOLLOW_SUCCESS, REMOVE_POST_OF_ME, UNFOLLOW_FAILURE, UNFOLLOW_REQUEST, UNFOLLOW_SUCCESS } from '../reducers/user';



function loadPostAPI(data) {
    return axios.get('/api/post', data)
}

function* loadPost(action) {
    try {

        // const result = yield call(addPostAPI,action.data)
        yield delay(1000);

        console.log(action.data+'data 뭐야 씨발')

        yield put({

            type: LOAD_POSTS_SUCCESS,
            data: generateDummyPost(10),

        });

    } catch (err) {

        yield put({

            type: LOAD_POSTS_FAILURE,
            data: err.response.data
        })
    }
}




function addPostAPI(data) {
    return axios.post('/api/post', data)
}

function* addPost(action) {
    try {

        // const result = yield call(addPostAPI,action.data)
        yield delay(1000);
        const id = shortid.generate();
        console.log(action.data)
        yield put({

            type: ADD_POST_SUCCESS,
            data: {
                content: action.data,
                id

            }

        });

        yield put({
            type: ADD_POST_TO_ME, data:id
        })
    } catch (err) {

        yield put({

            type: ADD_POST_FAILURE,
            data: err.response.data
        })
    }
}



function removePostAPI(data) {
    return axios.delete('/api/post', data)
}

function* removePost(action) {
    try {

        // const result = yield call(addPostAPI,action.data)
        yield delay(1000);
        yield put({

            type: REMOVE_POST_SUCCESS,
            data: action.data
        });

        yield put({
            type: REMOVE_POST_OF_ME, 
            data:action.data
        })
    } catch (err) {
        console.error(err)
        yield put({

            type: REMOVE_POST_FAILURE,
            data: err.response.data
        })
    }
}



function addCommentAPI(data) {
    return axios.post(`/api/post/${data.postId}/comment`, data)
}

function* addComment(action) {
    try {

        // const result = yield call(addCommentAPI,action.data)
        yield delay(1000);
        console.log('코멘트 사가')
        yield put({

            type: ADD_COMMENT_SUCCESS,
            data: action.data
        })
    } catch (err) {

        yield put({

            type: ADD_COMMENT_FAILURE,
            data: err.response.data
        })
    }

}


function* watchAddPost() {
    yield takeLatest(ADD_POST_REQUEST, addPost);
}
function* watchLoadPost() {
    yield throttle(5000,LOAD_POSTS_REQUEST, loadPost);
}
function* watchRemovePost() {
    yield takeLatest(REMOVE_POST_REQUEST, removePost);
}

function* watchAddComment() {
    yield takeLatest(ADD_COMMENT_REQUEST, addComment);
}


export default function* postSaga() {
    yield all([
        fork(watchAddPost),

        fork(watchLoadPost),
        fork(watchRemovePost),
        fork(watchAddComment),
    ])
}
