export const initialState={

    mainPosts: [{
        id:1,
        User:{
            id:1,
            nickname:'YJ',
        },
        content: '더미 데이터 #해시캐그 #익스프레스',
        Images:[
            {
            src:'https://i.ibb.co/DRpgMzr/FW6-Qq5qa-AAAu-Ba3.jpg'
        },
        {
            src:'https://ibb.co/DbtFD1K'
        },
        ],
        Comments:[
            {
            User:{
                nickname:'superman',
            },
            content: 'i wanna be',
        },
        {
            User:{
                nickname:'IRONMAN',
            },
            content: 'electorics',
        }],
    }],
    imagePaths:[],
    postAdded:false,

}

const ADD_POST = 'ADD_POST';
export const addPost = {
    type:ADD_POST
}

const dummyPost={
    id:2,
    content:'dummy data',
    User:{
        id:1,
        nickname:'YJ22'
    },
    Images:[],
    Comments:[],
}

const reducer = (state=initialState,action)=>{
    switch(action.type){
        case ADD_POST:
            return{
                ...state,
                mainPosts:[dummyPost,...state.mainPosts],
                postAdded:true,
            }

        default:
            return state;
    }
};



export default reducer;
