import axios from "axios";
import { all ,fork,takeLatest,delay,put,call} from "redux-saga/effects";
import { LOG_IN_SUCCESS,SIGN_UP_SUCCESS,SIGN_UP_REQUEST,FOLLOW_FAILURE,FOLLOW_REQUEST,FOLLOW_SUCCESS,LOG_IN_FAILURE,LOG_IN_REQUEST,LOG_OUT_FAILURE,LOG_OUT_REQUEST,LOG_OUT_SUCCESS,SIGN_UP_FAILURE, UNFOLLOW_REQUEST, UNFOLLOW_FAILURE, UNFOLLOW_SUCCESS } from "../reducers/user";




function logInAPI(data) {
    return axios.post('http://localhost:3065/user/login',data)
}

function* logIn(action) {
    try {
        console.log('saga login')
        const result = yield call(logInAPI,action.data)

        yield put({

            type: LOG_IN_SUCCESS,
            // data: result.data
            data:action.data
        })
    } catch (err) {

        yield put({

            type: LOG_IN_FAILURE,
            error: err.response.data
        })
    }

}


function logOutAPI() {
    return axios.post('/api/logout')
}

function* logOut() {
    try {

        // const result = yield call(logOutAPI)
        yield delay(1000);
        yield put({

            type: LOG_OUT_SUCCESS,
            // data: result.data
        })
    } catch (err) {

        yield put({

            type: LOG_OUT_FAILURE,
            error: err.response.data
        })
    }
}

function signUpAPI(data) {
    return axios.post('http://localhost:3065/user',data)
}

function* signUp(action) {
    try {

        const result = yield call(signUpAPI,action.data);
        console.log(result);

        yield put({

            type: SIGN_UP_SUCCESS,
            // data: result.data
        })
    } catch (err) {

        yield put({

            type: SIGN_UP_FAILURE,
            error: err.response.data
        })
    }
}

function followAPI(data) {
    return axios.post('/api/follow', data)
}

function* follow(action) {
    try {

        // const result = yield call(addPostAPI,action.data)
        yield delay(1000);
     
     
        yield put({

            type: FOLLOW_SUCCESS,
            data:  action.data,
             

        });

    } catch (err) {

        yield put({

            type: FOLLOW_FAILURE,
            data: err.response.data
        })
    }
}


function unfollowAPI(data) {
    return axios.post('/api/unfollow', data)
}

function* unfollow(action) {
    try {

        // const result = yield call(addPostAPI,action.data)
        yield delay(1000);


        yield put({

            type: UNFOLLOW_SUCCESS,
            data:  action.data,
             

        });

    } catch (err) {

        yield put({

            type: UNFOLLOW_FAILURE,
            data: err.response.data
        })
    }
}



function* watchFollow() {
    yield takeLatest(FOLLOW_REQUEST, follow);
}
function* watchUnfollow() {
    yield takeLatest(UNFOLLOW_REQUEST, unfollow);
}



function* watchLogIn() {
    yield takeLatest(LOG_IN_REQUEST, logIn);
}

function* watchLogOut() {
    yield takeLatest(LOG_OUT_REQUEST,logOut);
}

function* watchSignUp() {
    yield takeLatest(SIGN_UP_REQUEST,signUp);
}

export default function* userSaga(){
    yield all([
        fork(watchFollow),
        fork(watchUnfollow),
        fork(watchLogIn),
        fork(watchLogOut),
        fork(watchSignUp),

    ])
}
