//         type:'CHANGE_NICKNAME',
//         data,
//     }
// }



const rootReducer = ((state=initialState,action)=>{
    switch (action.type){ 
        case 'LOG_IN':
            return{
                ...state,
                user:{
                    ...state.user,
                    isLoggedIn:true,
                    user:action.data
                },
            };
            case 'LOG_OUT':
                return{
                    ...state,
                    user:{
                        ...state.user,
                        isLoggedIn:false,
                        user:null
                    },
                };
              
    }

});

export default rootReducer;
