import React from "react";
import { PropTypes } from "prop-types";
import Link from 'next/link';
import { Menu, Input, Row, Col } from 'antd';
import UserProfile from './UserProfile';
import LoginForm from './LoginForm';
import styled, { createGlobalStyle } from 'styled-components';
import { useSelector } from 'react-redux';

const SearchInput = styled(Input.Search)`
vertical-align: middle;
`
const Global = createGlobalStyle`
.ant-row{
    margin-right: 0 !important;
    margin-left: 0 !important;
}

.ant-col:first-child{
    padding-left:0 !important;
}

.ant-col:last-child{
    padding-right:0 !important;
}

`


const AppLayout = ({ children }) => {
    const {me} = useSelector((state) => state.user);

    return (
        <div>
            <Global/>
            <Menu mode='horizontal'>
                <Menu.Item key="1">
                    <Link href='/'><a>노드버드</a></Link>
                </Menu.Item>
                <Menu.Item key="2">
                    <Link href='/profile'><a>profile</a></Link>

                </Menu.Item>
                <Menu.Item key="3">
                    <SearchInput enterButton />
                </Menu.Item>
                <Menu.Item key="4">
                    <Link href='/signup'><a>signup</a></Link>
                </Menu.Item>
            </Menu>
            <Row gutter={8}>
                <Col xs={24} md={6}>
                    {me ? <UserProfile /> : <LoginForm />}
                </Col>
                <Col xs={24} md={12}>{children}</Col>
                <Col xs={24} md={6}>
                    <a href="https://www.naver.com" target="_blank" rel="noreferrer">Go to naver</a>
                </Col>
            </Row>

        </div>
    );
};


AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
};

export default AppLayout;

import React, { useCallback, useEffect, useState } from "react";
import PropTypes from 'prop-types';
import { Form, Button, Input } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { ADD_COMMENT_REQUEST } from "../reducers/post";

const CommentForm = ({ post }) => {
    const dispatch = useDispatch();
    const id = useSelector((state) => state.user.me?.id);
    const {addCommentDone,addCommentLoading} = useSelector((state) => state.post);
    // const [commentText,onChnageCommentText] = useInput('');
    const [commentText, setCommentText] = useState('');

    const onChnageCommentText = useCallback((e) => {
        setCommentText(e.target.value)
    }, [])

    useEffect(()=>{

        if(addCommentDone){
            setCommentText('');
        }
    },[addCommentDone])

    const onSubmitComment = useCallback(() => {
     
        dispatch({
            type: ADD_COMMENT_REQUEST,
            data: { content: commentText, postId: post.id, userId: id }
        })

    }, [commentText, id])

    return (
        <Form onFinish={onSubmitComment}>
            <Form.Item>
                <Input.TextArea
                    value={commentText}
                    onChange={onChnageCommentText}
                    rows={4}
                />
                <Button 
                
                style={{position:'absolute',right:0,bottom:-40,zIndex:1}}
                loading={addCommentLoading}
                type='primary' htmlType='submit'>삐약</Button>


            </Form.Item>
        </Form>
    )
}

CommentForm.prototype = {
    post: PropTypes.object.isRequired
}
export default CommentForm;

import React, { useCallback } from "react";
import PropTypes from'prop-types';
import {Button} from 'antd'
import { useDispatch, useSelector } from "react-redux";
import { FOLLOW_REQUEST, UNFOLLOW_REQUEST } from "../reducers/user";


const FollowButton=({post})=>{
    const dispatch = useDispatch();
    const {me,followLoading,unFollowLoading} = useSelector((state)=>state.user);

    const isFollowing = me?.Followings.find((v)=>v.id ===post.User.id);
const onClickButton = useCallback(()=>{
    if(isFollowing){
        dispatch({type:UNFOLLOW_REQUEST,data:post.User.id, })
    }else{
        dispatch({type:FOLLOW_REQUEST,data:post.User.id,})
        
    }
},[isFollowing]);

if(post.User.id ===me.id){
    return null;
}
return (
<Button loading={followLoading ||unFollowLoading } onClick={onClickButton}>
{isFollowing? '언팔로우':'팔로우'}
</Button>
);

}



FollowButton.propTypes={
    post:PropTypes.object.isRequired
}


export default FollowButton;

import PropTypes from 'prop-types';
import React, { useState } from 'react';
import Slick from 'react-slick';
import {Overlay,CloseBtn,Global,Header,ImageWrapper,Indicator,SlickWrapper,} from './styles';

const ImagesZoom = ({ images, onClose }) => {
    const [currentSlide, setCurrentSlide] = useState(0);
    return (
        <Overlay>
            <Global/>
            <Header>
                <h1>상세이미지</h1>
                <CloseBtn onClick={onClose}>X</CloseBtn>
            </Header>
            <SlickWrapper>
                <div>
                    <Slick
                        initialSlide={0}
                        beforeChange={(slide) => setCurrentSlide(slide)}
                        infinite
                        arrows={false}
                        slidesToScroll={1}
                    >
                        {images.map((v) => (
                            <ImageWrapper key={v.src}>
                                <img src={`http://localhost:3065/${v.src}`} alt={v.src} />
                            </ImageWrapper>
                        ))}
                    </Slick>
                    <Indicator>
                        <div>
                            {currentSlide +1}
                            {' '}
                            /
                            {images.length}
                        </div>
                    </Indicator>
                </div>
            </SlickWrapper>
        </Overlay>

    )
}


ImagesZoom.protoType = {
    images: PropTypes.arrayOf(PropTypes.object).isRequired,
    onClose: PropTypes.func.isRequired
}

export default ImagesZoom;

