import React, { useCallback, useEffect, useMemo, useState } from "react";
import { Form, Input, Button } from 'antd';
import Link from 'next/link';
import styled from 'styled-components';

import { useDispatch, useSelector } from 'react-redux';
import { loginRequestAction } from "../reducers/user";

const ButtonWrapper = styled.div`
    margin-top:10px;
`


const LoginForm = () => {
    const dispatch = useDispatch();
    const { logInLoading,logInError } = useSelector((state) => state.user);
    const [email, setEmail] = useState('');
    const onChangeEmail = useCallback((e) => {
        setEmail(e.target.value);

    }, [email])

    const [password, setPassword] = useState('');

    useEffect(()=>{
        if(logInError){
            alert(logInError);
        }
    },[logInError])
    const onChangePassword = useCallback((e) => {
        setPassword(e.target.value);

    }, [password])


    const style = useMemo(() => ({ marginTop: 10 }), []);

    const onSubmitForm = useCallback(() => {
        console.log(email, password);
        dispatch(loginRequestAction({ email, password }));
    }, [email, password])

    const FormWrapper = styled(Form)`
padding: 10px;
    `;


    return (
        <FormWrapper onFinish={onSubmitForm}>
            <div>
                <label htmlFor="user-email">이메일</label>
                <br />
                <Input name="user-email" type="email" value={email} onChange={onChangeEmail} required />

            </div>
            <div>
                <label htmlFor="user-password">password</label>
                <br />
                <Input
                    name="user-password"
                    type="password"
                    value={password}
                    onChange={onChangePassword} />

            </div>
            <ButtonWrapper style={style}>
                <Button type="primary" htmlType="submit" loading={logInLoading}>로그인</Button>
                <Link href="/signup"><a><Button>회원가입</Button></a></Link>

            </ButtonWrapper>
        </FormWrapper>
    );

}




export default LoginForm;

import { Card,Comment, List,Button, Popover, Avatar } from "antd";
import React, { useCallback, useEffect, useState } from "react";
import { RetweetOutlined, MessageOutlined, HeartOutlined, EllipsisOutlined, HeartTwoTone } from '@ant-design/icons';
import { useDispatch, useSelector } from "react-redux";
import PropTypes from 'prop-types';
import PostImages from "./PostImages";
import CommentForm from "./CommentForm";
import PostCardContent from "./PostCardContent";
import { REMOVE_POST_REQUEST,LIKE_POST_REQUEST,UNLIKE_POST_REQUEST ,RETWEET_REQUEST, RETWEET_FAILURE} from "../reducers/post";
import FollowButton from "./FollowButton";


const PostCard = ({ post }) => {
    const dispatch = useDispatch();
    const {removePostLoading} = useSelector((state)=>state.post);
    
    const id = useSelector((state) => state.user.me?.id);

    const [commentFormOpened, setCommentFormOpened] = useState(false);



    const onLike = useCallback(() => {
        if(!id){
            alert('로그인이 필요합니다.')
        }
        return  dispatch({
            type:LIKE_POST_REQUEST,
            data: post.id,
        })
    }, [id])
    const onUnlike = useCallback(() => {
        if(!id){
            alert('로그인이 필요합니다.')
        }
        return   dispatch({
            type:UNLIKE_POST_REQUEST,
            data: post.id,
        })
    }, [id])

    const onToggleCOmment = useCallback(() => {
        setCommentFormOpened((prev) => !prev)
    }, [])

const onRemovePost=useCallback(()=>{
    if(!id){
        alert('로그인이 필요합니다.')
    }
    return dispatch({
        type:REMOVE_POST_REQUEST,
        data:post.id
    })
},[])

const liked = post.Likers.find((v)=>v.id===id);

const onRetweet = useCallback(()=>{
    if(!id){
        alert('로그인이 필요합니다.')
    }
    return dispatch({
        type:RETWEET_REQUEST,
        data:post.id

    })
},[id])
    return (
        <div style={{ marginTop: 10 }}>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetweetOutlined key='retweet' onClick={onRetweet}/>,


                    liked&&liked
                        ? <HeartTwoTone twoToneColor="#eb2f96" key="heart" onClick={onUnlike} />
                        : <HeartOutlined key='heart'onClick={onLike} />,

                    <MessageOutlined key='commnet' onClick={onToggleCOmment} />,
                    <Popover key='more' content={(
                        <Button.Group>
                            {id && post.User.id === id ?
                                <>
                                    <Button>수정</Button>
                                    <Button 
                                    onClick={onRemovePost}
                                    loading={removePostLoading}
                                    type='danger'>삭제</Button>
                                </>
                                : <Button>신고</Button>}

                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                ]}
                title={post.RetweetId ? `${post.User.nickname}님이 리트윗하셨습니다`: null}
                extra={id&&<FollowButton post={post}/>}
            >
                {post.RetweetId && post.RetweetId
                    ?(
                        <Card
                        cover={post.Retweet.Images[0] && <PostImages images={post.Retweet.Images} />}
                        >
                                 <Card.Meta
                        avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                        title={post.User.nickname}
                        description={<PostCardContent postData={post.Retweet.content}/>}
                         />
                        </Card>
                    )
                    :(

                        <Card.Meta
                        avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                        title={post.User.nickname}
                        description={<PostCardContent postData={post.content}/>} />

                    )

                
                }
  
            </Card>
            {commentFormOpened && (
                <div>
                   <CommentForm post={post}/>
                   <List
                   header={`${post.Comments.length}개의 댓글`}
                   itemLayout="horizontal"
                   dataSource={post.Comments}
                   renderItem={(item)=>(
                    <li>
                        <Comment
                        author={item.User&&item.User.nickname}
                        avatar={item.User&&<Avatar>{item.User.nickname[0]}</Avatar>}
                        content={item.content}
                        />
                           
                    </li>
                   )}
                   />

                </div>)}
            {/* <CommentForm/>
    <Comments/> */}
        </div >
    )

}


PostCard.propTypes = {
    post: PropTypes.shape({
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createAt: PropTypes.string,
        Comments: PropTypes.arrayOf(PropTypes.object),
        Images: PropTypes.arrayOf(PropTypes.object),
        Likers: PropTypes.arrayOf(PropTypes.object),
        Retweetid:PropTypes.number,
        Retweet:PropTypes.objectOf(PropTypes.any),
    }).isRequired
}

export default PostCard;

import React from "react";
import PropTypes from 'prop-types';
import Link from 'next/link';

const PostCardContent =({postData})=>{

    return(

        <div>
            {postData.split(/(#[^\s#]+)/g).map((v,i)=>{
                if(v.match(/(#[^\s#]+)/)){
                    return <Link href={`/hashtag/${v.slice(1)}`} key={i}><a>{v}</a></Link>
                }
               return v; 
            })}
        </div>
    )
}



PostCardContent.propTypes = {
    postData: PropTypes.string.isRequired
}




export default PostCardContent;

import { Form, Input, Button } from 'antd';
import  React,{ useCallback, useEffect, useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addPost,UPLOAD_IMAGES_REQUEST,REMOVE_IMAGE, ADD_POST_REQUEST } from '../reducers/post';

const PostForm = () => {
    const [text,setText] = useState('');
    const dispatch = useDispatch();
    const { imagePaths,addPostDone } = useSelector((state) => state.post);
    const imageInput = useRef();
    useEffect(()=>{

        if(addPostDone){
            setText('');
        }
    },[addPostDone])



    const onChangeText =useCallback((e)=>{
        setText(e.target.value);
    })


    const onSubmit = useCallback(() => {
        if(!text || !text.trim()){
            return alert('게시글을 작성하세요.');
        }

        const formData = new FormData();
        imagePaths.forEach((p)=>{
            formData.append('image',p);

        });
        formData.append('content',text);

      return  dispatch({
            type:ADD_POST_REQUEST,
            data:formData
        })
       
    }, [text,imagePaths]);

    const onClickImageUpload=useCallback(()=>{
imageInput.current.click();
    },[imageInput.current])

const onChangeImages = useCallback((e)=>{
    console.log('images',e.target.files);
    const imageFormData = new FormData();
    [].forEach.call(e.target.files,(f)=>{
        imageFormData.append('image',f);
    });
    dispatch({
        type:UPLOAD_IMAGES_REQUEST,
        data:imageFormData
    })
})

const onRemoveImage=useCallback((index)=>()=>{
    dispatch({
        type: REMOVE_IMAGE,
        data:index
    })
})
    return (
        <Form style={{ margin: '10px 0 20px' }} encType="multipart/form-data" onFinish={onSubmit}>
            <Input.TextArea
                value={text}
                onChange={onChangeText}
                maxLength={140}
                placeholder='어떤 신기한 일이 있었나요?'
            />
            <div>
                <input type='file' name='image' multiple hidden ref={imageInput} onChange={onChangeImages} />
                <Button onClick={onClickImageUpload}>이미지 업로드</Button>
                <Button type='primary' style={{ float: 'right' }} htmlType='submit'>짹짹</Button>

            </div>
            <div>
                {imagePaths.map((v,i) => (
                    <div key={v} style={{ display: 'inline-block' }}>
                        <img src={`http://localhost:3065/${v}`} style={{ width: '200px' }} alt={v} />
                        <div>
                            <Button onClick={onRemoveImage(i)}>제거</Button>
                        </div>


                    </div>
                ))}
            </div>
        </Form>
    );

}

export default PostForm;

import React, { useCallback, useState } from "react";
import PropTypes from 'prop-types';
import { PlusOutlined} from '@ant-design/icons';
import ImagesZoom from "./ImagesZoom";


const PostImages = ({ images }) => {

    const [showImagesZoom, setShowImagesZoom] = useState(false);
    const onZoom = useCallback(() => {
        setShowImagesZoom(true);
    }, [])

    const onClose  = useCallback(()=>{
        setShowImagesZoom(false)
    },[])

    if (images.length === 1) {
        return (
            <>
                <img role='presentation' scr={`http://localhost:3065/${images[0].src}`} alt={images[0].src} onClick={onZoom} />
                {showImagesZoom && <ImagesZoom  images={images} onClose={onClose}/>}
            </>
        )
    }
    if (images.length === 2) {
        return (
            <>
                <img role='presentation' width="50%" scr={`http://localhost:3065/${images[0].src}`} alt={images[0].src} onClick={onZoom} />
                <img role='presentation' width="50%" scr={`http://localhost:3065/${images[1].src}`} alt={images[1].src} onClick={onZoom} />
                {showImagesZoom && <ImagesZoom  images={images} onClose={onClose}/>}

            </>
        )
    }
    return (
        <>
            <div>
                <img role='presentation' width="50%" scr={`http://localhost:3065/${images[0].src}`} alt={images[0].src} onClick={onZoom} />
                <div
                    role="presentation"
                    style={{ display: 'inline-block', width: '50%', textAlign: 'center', verticalAlign: 'middle' }}
                    onClick={onZoom}
                >
                    <PlusOutlined />
                    <br />
                    {images.length - 1}
                    개의 사진 더보기


                </div>
            </div>
            {showImagesZoom && <ImagesZoom  images={images} onClose={onClose}/>}

        </>
    )
}



PostImages.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object),
}

export default PostImages;

import React, { useCallback } from "react";
import { Card, Avatar, Button } from 'antd';
import {useDispatch, useSelector} from 'react-redux';
import { logoutRequestAction } from "../reducers/user";

const UserProfile = () => {
    const dispatch = useDispatch();
    const {me,logOutLoading} = useSelector((state)=>state.user);
const onLogOut = useCallback(()=>{
    dispatch(logoutRequestAction());
})

    return (
        <Card
            actions={[
                <div key="twit">짹쨱<br />{me.Posts.length}</div>,
                <div key="followings">followings<br />{me.Followings.length}</div>,
                <div key="followers">followers<br />{me.Followers.length}</div>,
            ]}
        >
            <Card.Meta
                title={me.nickname}
                avatar={<Avatar>{me.nickname[0]}</Avatar>}
            />
            <Button onClick={onLogOut} loading={logOutLoading}>로그아웃</Button>
        </Card>


    );
}



export default UserProfile;

